import React from 'react';
import './Home.css';
import PostCard from './PostCard';

const samplePosts = [
  {
    id: 1,
    title: 'iPhone 13 Pro Max, 256GB, Sierra Blue',
    price: '95,000',
    image: 'https://via.placeholder.com/250x150.png?text=iPhone+13',
    location: 'Mumbai, Maharashtra',
    date: 'Today',
  },
  {
    id: 2,
    title: 'Royal Enfield Classic 350, 2021 Model',
    price: '1,80,000',
    image: 'https://via.placeholder.com/250x150.png?text=RE+Classic+350',
    location: 'Delhi, Delhi',
    date: 'Yesterday',
  },
  {
    id: 3,
    title: 'Wooden Sofa Set (3+1+1) in good condition',
    price: '15,000',
    image: 'https://via.placeholder.com/250x150.png?text=Sofa+Set',
    location: 'Bangalore, Karnataka',
    date: '3 days ago',
  },
  {
    id: 4,
    title: 'Sony Bravia 4K UHD Smart TV 55 inch',
    price: '55,000',
    image: 'https://via.placeholder.com/250x150.png?text=Sony+TV',
    location: 'Pune, Maharashtra',
    date: 'Oct 28',
  },
];

function Home() {
  return (
    <div className="home">
      <div className="home__banner">
        {/* Banner content will go here */}
      </div>
      <div className="home__posts">
        <h2>Fresh Recommendations</h2>
        <div className="posts__container">
          {samplePosts.map((post) => (
            <PostCard key={post.id} post={post} />
          ))}
        </div>
      </div>
    </div>
  );
}

export default Home;
