import React from 'react';
import { useParams } from 'react-router-dom';
import { samplePosts } from '../data';
import './ProductPage.css';

function ProductPage() {
  const { id } = useParams();
  const post = samplePosts.find((p) => p.id === parseInt(id));

  if (!post) {
    return <div className="product-page"><h2>Product not found!</h2></div>;
  }

  return (
    <div className="product-page">
      <div className="product-page__main">
        <div className="product-page__image-container">
          <img src={post.image} alt={post.title} />
        </div>
        <div className="product-page__details">
          <h1 className="product-page__title">{post.title}</h1>
          <p className="product-page__price">â‚¹ {post.price}</p>
          <p className="product-page__location">{post.location}</p>
          <button className="contact-seller-btn">Contact Seller</button>
        </div>
      </div>
      <div className="product-page__description">
        <h2>Description</h2>
        <p>{post.description}</p>
      </div>
    </div>
  );
}

export default ProductPage;
